import React, {useEffect} from "react";
import Order from "../Order/Order";
import classes from './Orders.module.css';
import { Route } from "react-router";
/* import LoadingSpinner from "../../components/UI/LoadingSpinner"; */
import { Link } from 'react-router-dom';
import { Table } from "react-bootstrap";
import useHttp from '../../../../hooks/use-http'
import {getAllOrders} from '../../../../lib/api'

 const DUMMY_DATA = [

  ] 


const Orders = () => {
    const {sendRequest, status, data: loadedOrders, error} = useHttp(getAllOrders, true);
    useEffect(() => {
        sendRequest();
    }, [sendRequest])


    if(status === 'pending'){
        return (
           /*  <div className='centered'>
                <LoadingSpinner />
            </div> */
             null
        );
    }


    if(error){
        return <p className='centered focus'>{error}</p>
    }

    if(status === 'completed' && (!loadedOrders) || loadedOrders.length === 0){
        return <h1>No orders</h1>
    }
    return (
        <>

            <section className={classes.Orders}>
            <h3>List of orders</h3>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Order No.</th>
                            <th>Date</th>
                            <th>Customer</th>
                            <th>Email</th>
                            <th>Shipping Address</th>
                            <th>Sum, $</th>
                            <th>Status</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {DUMMY_DATA.map((order) => {
                            return (
                                <Order key={order.id}
                            id = {order.id}
                            {...order}
                            />
                            );
                        }
                        )}
                    </tbody>
                </Table>


                {/* <Link to='/orders/p1'> <Order /></Link>
           <Link to='/orders/p2'> <Order /></Link>
           <Link to='/orders/p3'> <Order /></Link>
           <Route path='/orders/newOrder'>
                <p>Welcome, new User</p>
                </Route> */}
            </section>
        </>
    );
};

export default Orders;
